  private void setSelected(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_setSelected
// Add your handling code here:
	System.out.println("set Selected");
	StandardListDialog dialog = new StandardListDialog(Source.getNameList());
	System.out.println("created dialog");
	dialog.setSize(Top.Defaults.dialogSizeX.getValue(),
                Top.Defaults.dialogSizeY.getValue());
	dialog.show();
	if(dialog.getReturnStatus() == dialog.RET_OK) {
	    String[] selected = dialog.listPanel.selectedItemsToString(true);
	    selectedString = selected[0];
	}
	doOperation(selectedString);
	System.out.println("did operation");
  }//GEN-LAST:event_setSelected

    public void setupList(java.lang.String title,ReactList source,TopReactionMenu top,java.awt.Dimension psize) {
	Top = top;
	Source = source;
	buttonTitle = title;
        listMoleculeButton.setText(buttonTitle);
	listPanel = new StandardListPanel(buttonTitle,Source.getNameList());
    }    

    public boolean doOperation(java.lang.String name) {
            return true;
    }
    
