  private void mechStandard(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_mechStandard
// Add your handling code here:
      String command = Top.reactionSetup.mechanismScriptDirectory.getValue() + 
                       Top.reactionSetup.standardMechanismSetup.getValue();
      Top.tLink.start(command);
      Top.tLink.execute("");
      Top.tLink.stop();
      Top.history.setHistoryLevel(3);
  }//GEN-LAST:event_mechStandard


